INSERT INTO `products` (`product_id`, `name`, `slug`, `cpu`, `ram`, `storage`, `display`, `vga`, `battery`, `weight`, `material`, `kind`, `condition`, `price`, `promotion`, `status`, `featured`, `warranty`, `description`, `img`, `category`, `created_at`, `updated_at`) VALUES
(2, 'HP Zbook 15G2', 'hp-zbook-15g2', 'Intel Core I7 - 4810MQ', '8', 'SSD 256GB', '15.6 Inch FHD', 'NVIDIA Quadro K1100', '8 Cells', '3 Kg', 'Kim loại và Hợp kim Magie', 'Thiết kế đồ họa', 'Old', 12000000, '11500000', 1, 1, '12 tháng 1 đổi 1', '<p><strong>Đ&aacute;nh gi&aacute; chi tiết Laptop HP ZBook 15 G2</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhắc đến HP, người d&ugrave;ng li&ecirc;n tưởng ngay đến HP Elitebook WorkStation, v&agrave; chắc hẳn ai cũng biết đến HP Zbook &ndash; Series mang tiếng vang mạnh mẽ tr&ecirc;n thị trường v&agrave; cũng đ&oacute;ng g&oacute;p kh&ocirc;ng nhỏ trong qu&aacute; tr&igrave;nh thay m&aacute;u cho d&ograve;ng sản phẩm HP Elitebook WorkStation.</p>\r\n\r\n<p>Zbook định gi&aacute; tr&ecirc;n thị trường với mức gi&aacute; kh&aacute; cao, với h&igrave;nh thức được coi l&agrave; kh&aacute; cồng kềnh v&agrave; nếu kh&ocirc;ng c&oacute; độ bền v&agrave; độ ổn định cũng như hiệu năng khủng của m&aacute;y k&eacute;o lại th&igrave; chắc chắn d&ograve;ng n&agrave;y đ&atilde; ho&agrave;n to&agrave;n biến mất tr&ecirc;n thị trường</p>\r\n\r\n<p>HP Zbook 15 G2 sẽ tiếp tục chứng minh được sự ph&aacute;t triển vượt trội của m&igrave;nh khi so với c&aacute;c &ldquo;đ&agrave;n anh đ&agrave;n chị&rdquo; bao gồm thiết kế &ldquo;hầm hố&rdquo;, h&igrave;nh thức b&ecirc;n ngo&agrave;i quyến rũ v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m tốt. Ngo&agrave;i ra, Model n&agrave;y được trang bị c&ugrave;ng với CPU Core i7 - 4810MQ, m&agrave;n h&igrave;nh Full HD, được thiết kế đa dạng c&aacute;c cổng kết nối. Ch&uacute;ng ta h&atilde;y c&ugrave;ng cảm nhận về d&ograve;ng sản phẩm n&agrave;y nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/product/2457_20170527_080734.jpg\" /></p>\r\n\r\n<p><strong>Cấu h&igrave;nh chi tiết:</strong></p>\r\n\r\n<p><strong>Bộ xử l&yacute;:</strong>&nbsp;Intel Core i7 4810MQ (8x2.8GHz, Turbo Boost 3.8GHz, 6MB Cache)</p>\r\n\r\n<p><strong>Bộ nhớ trong:</strong>&nbsp;8GB 1600 MHz</p>\r\n\r\n<p><strong>Ổ đĩa cứng:</strong>&nbsp;SSD 256GB</p>\r\n\r\n<p><strong>Cạc m&agrave;n h&igrave;nh:</strong>&nbsp;NVIDIA Quadro K1100M 2GB GDDR5</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh:</strong>&nbsp;15.6-inch Full HD (1920x1080p)</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;2.82kg d&agrave;y 30.5mm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế</strong></p>\r\n\r\n<p>Tương tự như c&aacute;c d&ograve;ng m&aacute;y HP Elitebook WorkStation, HP Zbook 15 được bao phủ bởi lớp hợp kim nh&ocirc;m tại mặt tr&ecirc;n của m&aacute;y, phần th&acirc;n dưới của m&aacute;y v&agrave; khu vực bản lề của m&aacute;y khiến cho sản phẩm HP Zbook 15 cực k&igrave; chắc chắn v&agrave; cứng c&aacute;p đủ sức chống lại c&aacute;c t&aacute;c động lớn của ngoại lực như va đập hay bị b&oacute;p m&eacute;o bởi. Zbook 15 c&ograve;n trải qua những cuộc thử nghiệm nghi&ecirc;m ngặt về độ bền v&agrave; đạt ti&ecirc;u chuẩn Mil-STD 810G của qu&acirc;n đội Mỹ.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/product/2457_20170527_080020.jpg\" /></p>\r\n\r\n<p>Một điểm kh&aacute;c biệt trong HP Zbook 15 l&agrave; sự thiết kế tinh tế ph&iacute;a viền nắp tr&ecirc;n của m&aacute;y bằng nhựa, bo tr&ograve;n xung quanh tạo n&ecirc;n sự quyến rũ kh&ocirc;ng thể chối từ. Viền nắp tr&ecirc;n của m&aacute;y c&ograve;n được xếp dọc những v&acirc;n thẳng đứng đen tuyền v&agrave; c&oacute; Logo của nh&agrave; sản xuất HP ở khu vực trung t&acirc;m b&oacute;ng lo&aacute;ng v&agrave; bắt mắt. Một điểm kh&ocirc;n kh&eacute;o Hp l&agrave; đang &ldquo;đ&aacute;nh lừa&rdquo; thị gi&aacute;c của người d&ugrave;ng trong sản phẩm Zbook 15. Mặc d&ugrave; trọng lượng của d&ograve;ng sản phẩm n&agrave;y kh&ocirc;ng ch&ecirc;nh lệch nhiều so với Dell Precision M4800 v&agrave; Thinkpad W530 nhưng nhờ thiết kế n&agrave;y, người d&ugrave;ng c&oacute; cảm gi&aacute;c như m&aacute;y c&oacute; trọng lượng nhỏ hơn rất nhiều.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_20170527_080548.jpg\" /></p>\r\n\r\n<p><strong>Cổng kết nối.</strong></p>\r\n\r\n<p>Zbook 15 c&oacute; đầu đủ c&aacute;c cổng kết nối của HP Elitebook WorkStation v&agrave; Dell Precision M4800 như cổng VGA v&agrave; thunderbolt (cổng n&agrave;y gi&uacute;p cho giao tiếp truyền tải dữ liệu nhanh hơn), cổng kết nối USB 2.0, cổng FireWire (giao tiếp chuẩn IEEE 1394 gi&uacute;p đường truyền dữ liệu ngoại vi lớn tới m&aacute;y nhanh hơn) v&agrave; cổng ESata..</p>\r\n\r\n<p>Mặt dưới của m&aacute;y l&agrave; cổng Docking để kết nối với c&aacute;c thiết bị b&ecirc;n ngo&agrave;i</p>\r\n\r\n<p>Cạnh phải: SD reader, jack tai nghe, 1x USB 3.0, ổ đĩa quang, VGA</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm__MG_4096_334a6a8e87.jpg\" /></p>\r\n\r\n<p>Cạnh tr&aacute;i: Kh&oacute;a Kensington, USB 2.0, cổng Thunderbolt, cổng DisplayPort, USB 3.0, 54 mm ExpressCard, Smartcard.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm__MG_4098_723e428d32.jpg\" /></p>\r\n\r\n<p>Ph&iacute;a sau m&aacute;y dường như rất &iacute;t c&aacute;c cổng kết nối v&agrave; chỉ c&oacute; cổng USB 2.0, cổng mạng, cổng nguồn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm__MG_4097_111f118a9b.jpg\" /></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p>Giống như c&aacute;c d&ograve;ng WorkStation d&ograve;ng sản phẩm n&agrave;y được trang bị m&agrave;n h&igrave;nh với độ ph&acirc;n giải m&agrave;n FullHD gi&uacute;p cho c&aacute;c g&oacute;c nh&igrave;n tốt hơn H&igrave;nh ảnh được xuất hiện r&otilde; r&agrave;ng v&agrave; kh&ocirc;ng bị b&oacute;ng mờ hay gặp vỡ h&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_Untitled.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong 1 cuộc đo lường độ s&aacute;ng m&agrave;n h&igrave;nh trung b&igrave;nh đạt tới 300nits. Độ tương phản của m&agrave;n h&igrave;nh l&agrave; 500:1 so s&aacute;nh gần bằng 1 nửa với Dell Precision M4800 v&agrave; n&oacute; l&agrave; vượt trội hơn hẳn so với Thinkpad W530 v&agrave; MSI GT 60.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_vsxps15_374362aeac.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_vsm6800_5eefa02ab2.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_vsgt60_ea3ef49c21.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_vsadobergb_0517623420.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_saturationpre_14a7eac043.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_saturationpost_ab95c5fae5.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_grayscalepre__4b8ff4bca2.png\" /><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm_grayscalepost_3d88c8c2c5.png\" /></p>\r\n\r\n<p>Khi sử dụng m&aacute;y dưới &aacute;nh s&aacute;ng mặt trời trực tiếp, m&agrave;n nh&aacute;m c&oacute; thể gi&uacute;p cho h&igrave;nh ảnh r&otilde; n&eacute;t v&agrave; chống ch&oacute;i. Tuy nhi&ecirc;n, sẽ kh&ocirc;ng đủ để ngăn cản &aacute;nh s&aacute;ng mặt trời qu&aacute; l&acirc;u v&agrave; đề xuất đưa ra cho người d&ugrave;ng l&agrave; n&ecirc;n giảm độ s&aacute;ng m&agrave;n h&igrave;nh hoặc thay đổi m&ocirc;i trường l&agrave;m việc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_2.png\" /></p>\r\n\r\n<p><strong>Loa</strong></p>\r\n\r\n<p>B&ecirc;n dưới m&agrave;n h&igrave;nh, giữa thanh bản lề l&agrave; loa, thiết kế dạng một dải rộng gồm c&aacute;c lỗ nhỏ. Đ&acirc;y l&agrave; điểm mới tr&ecirc;n ZBook 15 bởi loa của d&ograve;ng EliteBook Workstation trước đ&acirc;y thường được đặt dưới đ&aacute;y m&aacute;y, gần r&igrave;a trước. C&ocirc;ng nghệ loa m&agrave; HP sử dụng tr&ecirc;n HP ZBook 15 l&agrave; DTS thay v&igrave; SRS như phi&ecirc;n bản cũ. Chất lượng &acirc;m thanh của 2 loa Stereo kh&aacute; tốt, &acirc;m lượng to v&agrave; kh&ocirc;ng r&egrave; khi mở tối đa, hơi thiếu bass. Dĩ nhi&ecirc;n, l&agrave; một d&ograve;ng m&aacute;y trạm phục vụ cho m&ocirc;i trường doanh nghiệp th&igrave; &acirc;m thanh kh&ocirc;ng phải l&agrave; mục ti&ecirc;u quan t&acirc;m của HP như d&ograve;ng giải tr&iacute; Envy.</p>\r\n\r\n<p><strong>B&agrave;n ph&iacute;m&nbsp;</strong></p>\r\n\r\n<p>B&agrave;n ph&iacute;m của d&ograve;ng m&aacute;y n&agrave;y thường c&oacute; led ph&iacute;m v&agrave; k&iacute;ch thước tương đương với d&ograve;ng m&aacute;y Hp Zbook 17 . Trải nghiệm b&agrave;n ph&iacute;m rất đ&atilde;, người d&ugrave;ng cảm nhận được tr&ecirc;n từng đầu ng&oacute;n tay v&agrave; gần như kh&ocirc;ng tạo ra tiếng động trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm__MG_4089_7f366f32e5.jpg\" /></p>\r\n\r\n<p><strong>Touchpad v&agrave; Trackpoint</strong></p>\r\n\r\n<p>Mặc d&ugrave; Zbook 15 được thiết kế nhỏ hơn Hp Zbook 17 nhưng Touchpad của 2 d&ograve;ng sản phẩm n&agrave;y l&agrave; bằng nhau (103x59mm) v&agrave; c&ugrave;ng trải nghiệm touchpad với phần mềm Synaptics Luxpad Y1.6 cảm nhận được sự mềm mại v&agrave; ch&iacute;nh x&aacute;c khi di chuyển khiến người d&ugrave;ng y&ecirc;u th&iacute;ch hơn với d&ograve;ng sản phẩm n&agrave;y. Touchpad của m&aacute;y được chia l&agrave;m l&agrave;m 3 đều nhau khiến cho qu&aacute; tr&igrave;nh sử dụng&nbsp; được tiện lợi hơn l&agrave; bị l&otilde;m xuống một ch&uacute;t.</p>\r\n\r\n<p>Ngo&agrave;i ra, Touchpad của ZBook 15 cũng hỗ trợ nhiều cử chỉ cảm ứng kh&aacute;c nhau, v&iacute; dụ như cuộn bằng 2 ng&oacute;n tay v&agrave; k&eacute;o-để-zoom. Bạn c&oacute; thể k&eacute;o để ph&oacute;ng to/thu nhỏ c&aacute;c văn bản hoặc c&aacute;c biểu tượng tr&ecirc;n m&agrave;n h&igrave;nh desktop.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_csm__MG_4088_63f02e0b7d.jpg\" /></p>\r\n\r\n<p>Trackpoint của ZBook 15 c&oacute; bề mặt v&acirc;n cao su gi&uacute;p ng&oacute;n tay của bạn được thoải m&aacute;i m&agrave; kh&ocirc;ng bị trượt tay khi sử dụng. Trackpoint được đặt giữa ph&iacute;m G v&agrave; H, do đ&oacute; bạn sẽ &iacute;t khi phải di chuyển tay ra xa h&agrave;ng ph&iacute;m giữa nếu sử dụng pointing stick. Trackpoint c&oacute; bề mặt hơi l&otilde;m xuống v&agrave; rất nhiều chấm v&acirc;n cứng. Nh&igrave;n chung, Trackpoint của ZBook 15 sẽ mang lại trải nghiệm điều hướng rất ch&iacute;nh x&aacute;c. &nbsp;</p>\r\n\r\n<p><strong>Hệ thống bảo mật</strong></p>\r\n\r\n<p>Zbook 15 c&oacute; hệ thống bảo mật tối ưu hơn so với c&aacute;c d&ograve;ng m&aacute;y HP Elitebook WorkStation. Điều may mắn của Hp l&agrave; 1 d&ograve;ng m&aacute;y gi&agrave;nh cho doanh nh&acirc;n tr&ecirc;n to&agrave;n cầu n&ecirc;n thiết kế của m&aacute;y kh&aacute; phổ biến.</p>\r\n\r\n<p>M&aacute;y được thiết lập hệ thống bảo vệ phần cứng v&agrave; phần mềm của m&aacute;y bằng hệ thống bảo mật v&acirc;n tay v&agrave; khe cắm Smart Card được hỗ trợ bởi TPM 1.2 v&agrave; c&aacute;c phần mềm bảo vệ của HP v&agrave; Intel.</p>\r\n\r\n<p><strong>Hiệu suất</strong></p>\r\n\r\n<p>&nbsp;HP ZBook 15 c&oacute; thể vượt qua những những thử nghiệm Benchmark một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_4.png\" /></p>\r\n\r\n<p>Thử nghiệm đo tốc độ vi xử l&yacute; với Geekbench 3, HP ZBook 15 đạt điểm số tới 12.797. Điểm số n&agrave;y vượt qua điểm trung b&igrave;nh 9.280 của d&ograve;ng m&aacute;y t&iacute;nh x&aacute;ch tay ch&iacute;nh thống, nhưng lại k&eacute;m điểm số của Macbook Pro (13.652 điểm). Điều n&agrave;y cũng kh&ocirc;ng c&oacute; g&igrave; lạ khi HP ZBook sử dụng ổ cơ, c&ograve;n Macbook Pro sử dụng ổ cứng thể rắn SSD.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_8.png\" /></p>\r\n\r\n<p>Với card đồ họa rời NVIDIA Quadro K1100M, HP ZBook 15 c&oacute; thể chạy được những ứng dụng đồ họa một c&aacute;ch mượt m&agrave;. Ngo&agrave;i ra, với c&ocirc;ng nghệ Optimus của NVIDIA, m&aacute;y c&oacute; thể tự động chuyển đổi giữa card đồ họa rời v&agrave; card đồ họa t&iacute;ch hợp để tiết kiệm thời lượng pin.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_10.png\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, m&aacute;y lại mang lại một kết quả kh&aacute; đ&aacute;ng thất vọng trong thử nghiệm Benchmark khi chỉ đạt điểm số 1.277 tr&ecirc;n 3DMark 11, thấp hơn cả điểm số trung b&igrave;nh 1.391 tr&ecirc;n d&ograve;ng m&aacute;y ch&iacute;nh thống. Khi thử nghiệm với việc chơi game World of Warcraft, HP ZBook 15 chỉ đạt 42 khung h&igrave;nh/gi&acirc;y khi đặt c&agrave;i đặt tự động v&agrave; 23 khung h&igrave;nh/gi&acirc;y khi bật c&agrave;i đặt đồ họa tối đa.</p>\r\n\r\n<p><strong>Nhiệt độ</strong></p>\r\n\r\n<p>HP ZBook 15 giữ được nhiệt độ kh&aacute; thấp trong thử nghiệm. Touchpad c&oacute; nhiệt độ 27,8 độ C; phần giữa th&acirc;n m&aacute;y c&oacute; nhiệt độ 30,6 độ C; h&agrave;ng ph&iacute;m G v&agrave; H c&oacute; nhiệt độ 31,1 độ C. Chỉ c&oacute; phần khe tản nhiệt c&oacute; thể g&acirc;y kh&oacute; chịu cho bạn với nhiệt độ l&ecirc;n tới 35,6 độ C.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/2457_12.png\" /></p>\r\n\r\n<p><strong>Kết luận</strong></p>\r\n\r\n<p><em>Ưu điểm:&nbsp;</em><em>​</em></p>\r\n\r\n<p>-&nbsp;Thiết kế mới, nhẹ hơn phi&ecirc;n bản cũ, chắc chắn;</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh đẹp, g&oacute;c nh&igrave;n rộng;</p>\r\n\r\n<p>- Trải nghiệm g&otilde; ph&iacute;m v&agrave; b&agrave;n r&ecirc; tốt hơn;</p>\r\n\r\n<p>- Cấu h&igrave;nh cao, dễ n&acirc;ng cấp;</p>\r\n\r\n<p>- C&aacute;c kết nối cải tiến, c&oacute; th&ecirc;m cổng Thunderbolt;</p>\r\n\r\n<p>- Thời lượng pin kh&aacute; so với 1 chiếc m&aacute;y trạm.</p>\r\n\r\n<p><em>Nhược điểm:</em><em>​</em></p>\r\n\r\n<p>-&nbsp;Trọng lượng vẫn nặng, kh&oacute; mang theo;</p>\r\n\r\n<p>- Vỏ m&aacute;y dễ b&aacute;m v&acirc;n tay.</p>\r\n\r\n<p>- Gi&aacute; cao.</p>\r\n\r\n<p>Với b&agrave;n ph&iacute;m mỏng, dễ bấm hơn, touchpad tốt hơn, vận h&agrave;nh m&aacute;t mẻ, hiệu năng cao, ZBook 15 thật sự đ&atilde; mang lại trải nghiệm sử dụng tuyệt vời v&agrave; đ&uacute;ng với tinh thần hoạt động bền bỉ ng&agrave;y qua ng&agrave;y vốn c&oacute; của Workstation.&nbsp;Đ&acirc;y l&agrave; một chiếc m&aacute;y đ&aacute;ng c&acirc;n nhắc nếu bạn cần một chiếc m&aacute;y c&oacute; khả năng xử l&yacute; mạnh mẽ.</p>', 'HPZbook15G2.jpg', 1, '2019-05-21 06:40:21', '2019-05-23 10:21:29'),
(3, 'Dell Inspiron G5 5587', 'dell-inspiron-g5-5587', 'Intel Core i7 - 8750HQ', '8GB', 'SSD 128 GB + HDD 1 TB', '15.6 Inch FHD', 'Nvidia Geforce GTX 1050TI', '56 Wh', '2.7 Kg', 'Nhựa', 'Gaming', 'New', 25000000, '23500000', 1, 1, '24 tháng', '<p><strong>Dell G5 5587 - Laptop d&agrave;nh cho game thủ</strong></p>\r\n\r\n<p>Được nhận x&eacute;t l&agrave; một chiếc laptop gaming si&ecirc;u bền, si&ecirc;u khỏe, si&ecirc;u đẹp v&agrave; si&ecirc;u rẻ, Dell G5 5587 rất được l&ograve;ng c&aacute;c game thủ v&agrave; trở th&agrave;nh một trong những mẫu m&aacute;y gaming rất đ&aacute;ng ch&uacute; &yacute; trong thời gian vừa qua.</p>\r\n\r\n<p><strong>Cấu h&igrave;nh chi tiết</strong></p>\r\n\r\n<ul>\r\n	<li>CPU: Intel Core I7 8750H</li>\r\n	<li>Ram: 8GB DDR4</li>\r\n	<li>SSD: 128GB</li>\r\n	<li>HDD: 1TB</li>\r\n	<li>Card đồ họa: Intel HD Graphics 4600 + NVIDIA GeForce GTX 1050ti 4GB GDDR5</li>\r\n	<li>M&agrave;n h&igrave;nh: 15.6 inch Full HD</li>\r\n</ul>\r\n\r\n<p><strong>Thiết kế</strong></p>\r\n\r\n<p>Thiết kế của chiếc m&aacute;y Dell G5 5587 cực đẹp, hầm hố, vừa mang chất Gaming lại vừa sang trọng với t&ocirc;ng m&agrave;u chủ đạo l&agrave; đen kết hợp c&aacute;c chi tiết trang tr&iacute; m&agrave;u đỏ. Mặt trước của m&aacute;y c&oacute; chất liệu nhựa mịn, kh&ocirc;ng b&aacute;m v&acirc;n tay v&agrave; mồ h&ocirc;i, ch&iacute;nh giữa l&agrave; logo m&agrave;u đỏ đặc trưng của d&ograve;ng laptop Gaming của Dell. Mặt sau của m&aacute;y c&oacute; thiết kế rất tối giản với 2 ch&acirc;n cao su trải d&agrave;i ở ph&iacute;a tr&ecirc;n v&agrave; dưới m&aacute;y để chống trơn trượt, ở giữa sẽ l&agrave; c&aacute;c khe h&uacute;t gi&oacute; nhỏ.</p>\r\n\r\n<p>Điểm đặc biệt của m&aacute;y l&agrave; khe quạt tản nhiệt ở ph&iacute;a sau m&aacute;y cực hầm hố, vừa nh&igrave;n đ&atilde; c&oacute; ngay cảm nhận của những chiếc laptop gaming, mang lại vẻ khỏe khoắn, năng động.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/4008_DellG555871.jpg\" /></p>\r\n\r\n<p><strong>B&agrave;n ph&iacute;m, touchpad</strong></p>\r\n\r\n<p>B&agrave;n ph&iacute;m được chế tạo với độ ch&iacute;nh x&aacute;c cao, bộ khung cực chắc chắn khiến cho việc g&otilde; ph&iacute;m kh&ocirc;ng bị hiện tượng flex. Ph&iacute;m Fullsize gồm cả phần ph&iacute;m số vừa hỗ trợ chơi game vừa l&agrave;m việc hiệu quả, đặc biệt l&agrave; những c&ocirc;ng việc li&ecirc;n quan đến nhập liệu.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, với một m&aacute;y t&iacute;nh chơi game th&igrave; ph&iacute;m của chiếc m&aacute;y n&agrave;y kh&ocirc;ng được đ&aacute;nh gi&aacute; qu&aacute; cao do khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m hơi ngắn. Nhưng b&ugrave; lại, b&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền backlit m&agrave;u đỏ, vừa hỗ trợ l&agrave;m việc v&agrave; chơi game trong b&oacute;ng tối, vừa đem lại cảm gi&aacute;c gaming si&ecirc;u ngầu cho chiếc m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/4008_DellG555872.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p>Dell G5 5587 c&oacute; m&agrave;n h&igrave;nh Full HD với tấm nền IPS, 15,6 inch v&agrave; độ ph&acirc;n giải 1920 x 1080 pixels. Tỉ lệ m&agrave;n h&igrave;nh l&agrave; 16:9 v&agrave; mật độ pixel đo được l&agrave; 142 ppi. Độ s&aacute;ng tối đa đo được l&agrave; 230 nits với độ lệch m&agrave;u tối đa l&agrave; 14%. Nhiệt độ m&agrave;u tương ứng tr&ecirc;n m&agrave;n h&igrave;nh trắng l&agrave; 7100K. Với những chỉ số như vậy, tuy kh&ocirc;ng phải l&agrave; những con số qu&aacute; ấn tượng tuy nhi&ecirc;n m&aacute;y vẫn đủ mang lại một m&agrave;n h&igrave;nh với h&igrave;nh ảnh sắc n&eacute;t, sống động, ch&acirc;n thực.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/4008_DellG555874.jpg\" /></p>\r\n\r\n<p><strong>Cấu h&igrave;nh</strong></p>\r\n\r\n<p>Cấu h&igrave;nh SI&Ecirc;U KHOẺ với chip core i7 8750H, RAM 8GB, SSD 128GB v&agrave; HDD 1TB dung lượng cực khủng, m&aacute;y c&oacute; 2 khe cắm RAM gi&uacute;p bạn c&oacute; thể dễ d&agrave;ng n&acirc;ng cấp RAM l&ecirc;n đến 32GB. Đặc biệt, m&aacute;y vừa sử dụng card đồ họa rời Nvidia GeForce GTX 1050ti kết hợp c&ugrave;ng Intel HD Graphics 630 sẽ đem lại trải nghiệm chơi game cực ấn tượng.</p>\r\n\r\n<p>Với một số tựa game thịnh h&agrave;nh như Battlefield 1, m&aacute;y sẽ đạt mức FPS từ 55-65 FPS ở thiết lập Medium. Với c&aacute;c tựa game như CS Go v&agrave; Overwatch m&aacute;y chiến tốt hơn rất nhiều khi FPS duy tr&igrave; ở mức 85-95 FPS ở Ultra Settings. Đ&acirc;y l&agrave; một mức FPS rất l&yacute; tưởng đối với c&aacute;c Game thủ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/4008_DellG555875.jpeg\" /></p>\r\n\r\n<p><strong>Tản nhiệt</strong></p>\r\n\r\n<p>Một điểm đ&aacute;ng ch&uacute; &yacute; với Dell G5 5587 nữa l&agrave; quạt tản nhiệt. Khe tản nhiệt với 2 quạt tản nhiệt lớn, được thiết kế kh&aacute; hầm hố v&agrave; hướng về ph&iacute;a sau kh&ocirc;ng những mang lại hiệu quả l&agrave;m m&aacute;t vượt trội m&agrave; c&ograve;n gi&uacute;p người d&ugrave;ng &iacute;t c&oacute; cảm gi&aacute;c nhiệt l&ecirc;n tay, kh&ocirc;ng g&acirc;y kh&oacute; chịu khi chơi game li&ecirc;n tục.</p>\r\n\r\n<p><img alt=\"\" src=\"https://laptop88.vn/media/lib/4008_DellG555876.jpg\" /></p>\r\n\r\n<p><strong>Kết luận</strong></p>\r\n\r\n<p>Dell G5 5587 l&agrave; một chiếc laptop đ&aacute;ng mua với những bạn y&ecirc;u th&iacute;ch chơi game lại vừa muốn sử dụng m&aacute;y cho những c&ocirc;ng việc văn ph&ograve;ng thường ng&agrave;y. Với gi&aacute; chưa đến 30 triệu cho một chiếc m&aacute;y mới tinh chưa đập hộp, đ&acirc;y gần như l&agrave; chiếc m&aacute;y c&oacute; gi&aacute; kh&aacute; rẻ trong những d&ograve;ng m&aacute;y gaming th&ocirc;ng thường. Để đ&aacute;nh gi&aacute; chi tiết hơn về m&aacute;y, h&atilde;y mua 1 chiếc để tự cảm nhận v&agrave; c&oacute; những trải nghiệm ri&ecirc;ng của bản th&acirc;n m&igrave;nh nh&eacute;!</p>', 'DellG5.jpg', 2, '2019-05-21 06:50:35', '2019-05-23 10:21:20'),
(4, 'Acer Nitro 5 2019', 'acer-nitro-5-2019', 'Intel Core i5 - 8300H', '8GB DDR4', '1TB HDD', '15.6 Inch FHD IPS', 'nVidia Geforce GTX1650 4GB DDR5', '4 Cells', '2.5 Kg', 'Nhựa', 'Gaming', 'New', 25500000, '25000000', 1, 1, '24 tháng', '<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-1.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>NITRO 5 2019&nbsp;HO&Agrave;N TO&Agrave;N MỚI</h2>\r\n\r\n<h2>VŨ KH&Iacute; TẢN NHIỆT</h2>\r\n\r\n<p>Trải nghiệm ho&agrave;n to&agrave;n mới mẻ d&agrave;nh cho game thủ với&nbsp;<strong>Nitro 5 2019 AN515-54-59WX</strong>&nbsp;đ&oacute; l&agrave; sự ho&agrave;n hảo đến từ&nbsp;Vũ Kh&iacute; Tản Nhiệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>TRẢI NGHIỆM GAME TỐT HƠN</h2>\r\n\r\n<p><strong>Nitro 5 2019 AN515-54-59WX</strong>&nbsp;c&oacute; với cấu h&igrave;nh&nbsp;<strong>CPU Intel 8th</strong>&nbsp;v&agrave;&nbsp;<strong>VGA NVIDIA</strong>&nbsp;<strong>1650 4GB&nbsp;</strong>thế hệ mới nhất.</p>\r\n\r\n<p>Khả năng n&acirc;ng cấp tuyệt vời với ram l&ecirc;n đến 32 GB, lưu trữ l&ecirc;n đến 2TB HDD v&agrave; 1TB SSD PCIe NVMe.<img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-2.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>THAO T&Aacute;C NHANH HƠN</h2>\r\n\r\n<p>Với ph&iacute;m tắt Nitro Sense được bổ sung tr&ecirc;n&nbsp;<strong>Nitro 5 2019 AN515-54-59WX</strong>&nbsp;cho ph&eacute;p Game Thủ v&agrave;o phần mền kiểm so&aacute;t nhiệt độ Nitro Sense chỉ bằng một n&uacute;t chạm.</p>\r\n\r\n<p>Acer hiểu đối với Game Thủ 1 gi&acirc;y cũng cực k&igrave; quan trọng, n&ecirc;n việc bổ sung ph&iacute;m tắt Nitro Sense sẽ gi&uacute;p trải nghiệm Gaming trở n&ecirc;n ho&agrave;n hảo v&agrave; trực quan hơn bao giờ hết.</p>\r\n\r\n<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-3.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>NITRO SENSE</h2>\r\n\r\n<p>T&iacute;ch hợp sẵn trong phần mền Nitro Sense l&agrave; chức năng CoolBoost với ba chế độ tăng tốc quạt v&agrave; gi&uacute;p m&aacute;y giảm nhiệt độ ngay lập tức.</p>\r\n\r\n<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-4.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>KẾT NỐI NHANH HƠN</h2>\r\n\r\n<p><strong>Nitro 5 2019 AN515-54-59WX</strong>&nbsp;được Acer trang bị&nbsp;<strong>Killer&trade; Ethernet E2500</strong>.&nbsp;<strong>Killer&trade; Ethernet E2500</strong>&nbsp;gi&uacute;p mở rộng băng th&ocirc;ng xử l&yacute; đến 1Gbps.&nbsp;<strong>Gigabit Wi-Fi 5with 2&times;2 MU-MIMO Technology Intel 802.11ac Wave 2&nbsp;</strong>với băng tần k&eacute;p&nbsp;mới nhất c&oacute; tốc độ truyền dữ liệu l&ecirc;n đến 1.7Gbps.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-5.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>G&Oacute;C RỘNG HƠN</h2>\r\n\r\n<h2>TRẢI NGHIỆM TỐT HƠN</h2>\r\n\r\n<p>M&agrave;n h&igrave;nh FullHD IPS với viền m&agrave;n h&igrave;nh si&ecirc;u mỏng 7.18mm c&ugrave;ng tỉ lệ v&agrave;ng screen-to-body ratio l&ecirc;n đến 80%.<img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-6.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>&ldquo;ACT COOL&rdquo; HƠN &ndash; NGẦU HƠN</h2>\r\n\r\n<p>Vẻ ngo&agrave;i&nbsp;<strong>Nitro 5 2019 AN515-54-59WX</strong>&nbsp;sẽ ngầu hơn rất nhiều, đậm chất Gaming hơn. Tổng thể thiết kế được phối bởi hai m&agrave;u đen-đỏ chủ đạo, thể hiện r&otilde; n&eacute;t sự trầm tĩnh v&agrave; thế chủ đạo trong mọi cuộc chiến. Bản lề m&agrave;u đỏ rực rỡ tr&ecirc;n nền đen (<strong>Osidian Black</strong>), cụm&nbsp;<strong>WASD</strong>&nbsp;v&agrave; ph&iacute;m điều hướng cũng được l&agrave;m cực k&igrave; nổi bật.</p>\r\n\r\n<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-7.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<h2>NGHE GAMING HƠN</h2>\r\n\r\n<p>Chất lượng &acirc;m thanh được xử l&yacute; bằng phần mềm Waves MaxxAudio&reg; v&agrave; Acer TrueHarmony&trade; đưa chất lượng &acirc;m thanh ph&ograve;ng thu đến với bạn. Bạn sẽ ở trong kh&ocirc;ng gian sống động của c&aacute;c cuộc đua nghẹt thở.<img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2019/04/nitro-5-2019-8.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://xgear.vn/wp-content/uploads/2018/05/nitro5-8.jpg\" style=\"height:540px; width:960px\" /></p>', 'AcerNitro5.jpg', 6, '2019-05-21 07:00:01', '2019-05-29 19:39:03'),
(5, 'Asus Vivobook X507UF-EJ074T', 'asus-vivobook-x507uf-ej074t', 'Intel Core i7 8550U', '4GB DDR4', '1TB HDD', '15.6 Inch FHD', 'Nvidia MX130', '3 Cells (33Wh)', '1.68 Kg', 'Nhựa', 'Giải trí - Văn phòng', 'New', 16600000, 'Tặng RAM Kingspec DDR4 - 4GB', 1, 1, '24 tháng', '<p><strong>Đ&aacute;nh gi&aacute;&nbsp; Laptop Asus&nbsp;X507UF-EJ074T</strong></p>\r\n\r\n<p>ASUS Vivobook X507UF (i7-8550U) l&agrave; chiếc laptop hướng đến đối tượng sinh vi&ecirc;n hoặc những nh&acirc;n vi&ecirc;n văn ph&ograve;ng l&agrave; chủ yếu. Bởi n&oacute; đ&aacute;p ứng rất tốt c&aacute;c nhu cầu giải tr&iacute;, c&ocirc;ng việc v&agrave; học tập cơ bản. V&agrave; điều quan trọng nhất, đ&acirc;y l&agrave; một sự lựa chọn v&ocirc; c&ugrave;ng tiết kiệm hầu bao, đổi lại với một sản phẩm &ldquo;kh&ocirc;ng hề tồi&rdquo; một ch&uacute;t n&agrave;o.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng nhau điểm qua một số đặc điểm kĩ thuật của Asus Vivobook X507UF (i7-8550U)</p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_230ba4ff0224ad65235a2daf793cc7f0.jpg\" style=\"height:461px; width:750px\" /></p>\r\n\r\n<p><strong>Th&ocirc;ng số chi tiết</strong></p>\r\n\r\n<p>CPU: Intel Core i7 - 8550U (4 x 1.6GHz). Turbo: 3.40 GHz<br />\r\nRAM: 1 x 4GB DDR4&nbsp;<br />\r\nSố khe RAM: 2<br />\r\nCard đồ họa:&nbsp;Nvidia MX130&nbsp;<br />\r\nỔ cứng HDD: 1TB SATA 3<br />\r\nM&agrave;n h&igrave;nh: 15.6 Inch Full HD<br />\r\nC&acirc;n nặng: 1.68kg<br />\r\nPin 3 Cells (33Wh)</p>\r\n\r\n<p><strong>Thiết kế nhỏ gọn</strong></p>\r\n\r\n<p><strong><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_4_16_3-1024x651.jpg\" style=\"height:651px; width:1024px\" /></strong></p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; 1 c&aacute;ch tổng quan của m&igrave;nh, thiết kế của Asus Vivobook X507UF kh&ocirc;ng qu&aacute; d&agrave;y cũng kh&ocirc;ng qu&aacute; mỏng, n&oacute; đủ vừa để c&oacute; thể nh&eacute;t vừa hầu hết c&aacute;c balo, cặp l&agrave;m việc (kh&ocirc;ng t&iacute;nh c&aacute;c loại balo hay cặp nhỏ qu&aacute; nh&eacute;), thiết kế tổng qu&aacute;t l&agrave; d&agrave;i 365mm, rộng 266mm v&agrave; độ mỏng 21,9mm. Chất liệu sử dụng để tạo n&ecirc;n bộ khung của chiếc m&aacute;y n&agrave;y l&agrave; nhựa, n&ecirc;n trọng lượng rất nhẹ, chỉ 1.68Kg n&ecirc;n việc bạn cầm tr&ecirc;n tay di chuyển tương đối dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_7_8_8-1024x651.jpg\" style=\"height:651px; width:1024px\" /></p>\r\n\r\n<p>Phần lưng m&aacute;y được thiết kếkiểu sọc viền ch&igrave;m đơn giản với logo Asus quen thuộc ở ch&iacute;nh giữa. Ta sẽ kh&ocirc;ng thấy c&aacute;c hoa văn tr&ograve;n đồng t&acirc;m kiểu Zen đẹp mắt như tr&ecirc;n c&aacute;c laptop cấp cao hơn của Asus như Zenbook Flip 14. Nhưng nh&igrave;n chung, thiết kế nh&igrave;n từ b&ecirc;n ngo&agrave;i tr&ocirc;ng vẫn sang trọng, tinh tế v&agrave; ph&ugrave; hợp với mọi đối tượng ti&ecirc;u d&ugrave;ng, trong đ&oacute; đặc biệt l&agrave; sinh vi&ecirc;n.</p>\r\n\r\n<p><strong>Cổng kết nối</strong></p>\r\n\r\n<p><strong><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_HASP-ASUS-X507U-BR167T-1-1.jpg\" style=\"height:511px; width:960px\" /></strong></p>\r\n\r\n<p>Cạnh tr&aacute;i của m&aacute;y</p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_HASP-ASUS-X507U-BR167T-21-1.jpg\" style=\"height:597px; width:957px\" />Cạnh phải của m&aacute;y<br />\r\nC&aacute;c cạnh b&ecirc;n của laptop trang bị đầy đủ c&aacute;c cổng kết nối mở rộng bao gồm: 2 cổng USB 2.0, 1 cổng USB 3.0, 1 cổng HDMI, cổng sạc, khe đọc thẻ MicroSD v&agrave; 1 jack cắm 3.5mm tai nghe gi&uacute;p bạn l&agrave;m việc thoải m&aacute;i kh&ocirc;ng cần lo lắng về vấn đề kết nối với c&aacute;c thiết bị ngoại vi.<br />\r\n<br />\r\nPhần đ&aacute;y của m&aacute;y c&oacute; 4 nệm cao su đặt ở 4 g&oacute;c, c&ugrave;ng với đ&oacute; l&agrave; bộ đ&ocirc;i loa được thiết kế hướng xuống ph&iacute;a dưới, điều n&agrave;y sẽ gi&uacute;p khuếch đại &acirc;m thanh hơn so với c&aacute;c loa được thiết kế hướng l&ecirc;n tr&ecirc;n hoặc sang 2 b&ecirc;n.</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_laptop-asus-x407ma-bv043t-1.jpeg\" style=\"height:341px; width:500px\" /></p>\r\n\r\n<p>Asus Vivobook X507UF trang bị m&agrave;n h&igrave;nh 15.6 inch với độ ph&acirc;n giải Full HD (1920 x 1080). Hai th&ocirc;ng số n&agrave;y được Asus thiết kế rất hợp l&yacute; gi&uacute;p h&igrave;nh ảnh c&oacute; độ n&eacute;t vừa vặn, trải nghiệm xem phim tuyệt vời. Độ hiển thị của X507UF l&agrave; tốt hơn đối thủ c&ugrave;ng ph&acirc;n kh&uacute;c Dell Vostro 3578 , tuy cả 2 c&oacute; c&ugrave;ng k&iacute;ch thước m&agrave;n h&igrave;nh, nhưng Dell Vostro 3578 lại chỉ c&oacute; độ ph&acirc;n giải HD.</p>\r\n\r\n<p>Với viền mỏng 8,1mm v&agrave; tỷ lệ m&agrave;n h&igrave;nh tr&ecirc;n th&acirc;n m&aacute;y 75,4%, m&agrave;n h&igrave;nh NanoEdge tr&ecirc;n X507UF mang đến cho bạn kh&ocirc;ng gian hiển thị lớn hơn, đem lại trải nghiệm xem phim đắm ch&igrave;m hơn. Ngo&agrave;i ra, viền mỏng NanoEdge cũng l&agrave;m tăng t&iacute;nh thẩm mĩ cho m&aacute;y.<br />\r\nM&agrave;n h&igrave;nh của Asus Vivobook X507UF được trang bị tấm nền IPS chống lo&aacute;, gi&uacute;p bạn l&agrave;m việc ngo&agrave;i trời hiệu quả hơn. Kh&ocirc;ng những vậy, Asus c&ograve;n trang bị th&ecirc;m nhiều c&ocirc;ng nghệ h&igrave;nh ảnh kh&aacute;c như:</p>\r\n\r\n<p>&ndash; C&ocirc;ng nghệ tối ưu h&oacute;a h&igrave;nh ảnh ASUS Splendid với 4 chế độ hiển thị kh&aacute;c nhau: Chế độ Th&ocirc;ng thường l&agrave; lựa chọn l&yacute; tưởng cho c&aacute;c t&aacute;c vụ thường ng&agrave;y; chế độ Sống động sẽ gi&uacute;p tối ưu độ tương phản để đem đến những bức ảnh v&agrave; video tuyệt đẹp; chế độEye Care giảm thiểu &aacute;nh s&aacute;ng xanh dương để tr&aacute;nh mỏi mắt; v&agrave; chế độ Thủ c&ocirc;ng cho ph&eacute;p bạn t&ugrave;y chỉnh m&agrave;u sắc theo sở th&iacute;ch c&aacute; nh&acirc;n.</p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_8dd-1.jpg\" style=\"height:591px; width:1000px\" /></p>\r\n\r\n<p>4 chế độ hiển thị m&agrave;u sắc của ASUS Splendid<br />\r\n&ndash; C&ocirc;ng nghệ ASUS Tru2Life Video: tối ưu h&oacute;a th&ocirc;ng minh từng điểm ảnh trong khung h&igrave;nh video để tăng độ sắc n&eacute;t v&agrave; độ tương phản th&ecirc;m 150%, nhờ đ&oacute; bạn sẽ được tận hưởng chất lượng h&igrave;nh ảnh tốt nhất.</p>\r\n\r\n<p><strong>B&agrave;n ph&iacute;m v&agrave; TouchPad</strong></p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_HASP-ASUS-X507U-BR167T-6-1.jpg\" style=\"height:600px; width:956px\" /></p>\r\n\r\n<p>Asus X507UF được trang bị một b&agrave;n ph&iacute;m Full Size với c&aacute;c ph&iacute;m bấm lớn c&oacute; layout được bố tr&iacute; hợp l&yacute;. Việc thiết kế bề mặt ph&iacute;m hơi l&otilde;m xuống cũng g&oacute;p phần cải thiện cảm gi&aacute;c g&otilde; tr&ecirc;n thiết bị. Trải nghiệm sơ bộ, hệ thống ph&iacute;m n&agrave;y đem đến cảm gi&aacute;c g&otilde; tương đối thoải m&aacute;i nhờ h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u v&agrave; độ đ&agrave;n hồi tốt, bạn cũng sẽ kh&ocirc;ng mất qu&aacute; nhiều thời gian để l&agrave;m quen với b&agrave;n ph&iacute;m n&agrave;y.</p>\r\n\r\n<p><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_images-1-1.jpg\" style=\"height:168px; width:300px\" /></p>\r\n\r\n<p>Touchpad phủ k&iacute;nh của Asus X507UF lớn hơn so với c&aacute;c loại laptop th&ocirc;ng thường gi&uacute;p việc điều khiển trở n&ecirc;n mượt m&agrave; v&agrave; chuẩn x&aacute;c. Hỗ trợ thao t&aacute;c th&ocirc;ng minh như thao t&aacute;c 2 ng&oacute;n, 1 chạm,&hellip; gi&uacute;p bạn giải quyết c&ocirc;ng việc nhanh ch&oacute;ng v&agrave; tiện lợi. V&igrave; bề mặt phủ k&iacute;nh n&ecirc;n cảm gi&aacute;c chạm cũng sẽ bị ảnh hưởng khi tay bạn c&oacute; nhiều mồ h&ocirc;i, v&agrave; n&oacute; cũng dễ bẩn hơn, do vậy bạn n&ecirc;n vệ sinh touchpad thường xuy&ecirc;n h&agrave;ng ng&agrave;y.<br />\r\n<br />\r\nD&ugrave; mức gi&aacute; rất rẻ nhưng Asus Vivobook X507UF vẫn được t&iacute;ch hợp cảm biến v&acirc;n tay một chạm rất tiện lợi. Cảm biến n&agrave;y nằm ở g&oacute;c phải của Touchpad.</p>\r\n\r\n<p><strong>&Acirc;m thanh</strong><br />\r\nVới việc được trang bị hệ thống &acirc;m thanh SonicMaster do ch&iacute;nh Asus nghi&ecirc;n cứu v&agrave; ph&aacute;t triển, Asus Vivobook X507UF đem đến chất lượng &acirc;m thanh c&oacute; thể n&oacute;i l&agrave; rất tốt trong tầm gi&aacute;. &Acirc;m thanh ph&aacute;t ra to v&agrave; r&otilde;, &acirc;m bass d&ugrave; chưa đủ lực nhưng c&oacute; chiều s&acirc;u, mid v&agrave; treble cũng được thể hiện ở mức độ tương đối, từ đ&oacute; đem đến sự thoải m&aacute;i cho người nghe trong từng ho&agrave;n cảnh như xem phim hay giải tr&iacute;.</p>\r\n\r\n<p><strong>Hiệu năng</strong><br />\r\nAsus Vivobook X507UF được trang bị bộ vi xử l&yacute; thế hệ thứ 8 của Intel l&agrave; Core i5-8250U tốc độ 1.6 Ghz (boost l&ecirc;n đến 3.4Ghz) cho khả năng xử l&yacute; t&aacute;c vụ văn ph&ograve;ng, lướt web, xem phim tốt v&agrave; ổn định. M&aacute;y chỉ được trang bị RAM 4 GB DDR4 n&ecirc;n sẽ bị hạn chế khi l&agrave;m việc với nhiều chương tr&igrave;nh hoặc mở nhiều tab Chrome. Ổ cứng HDD c&oacute; dung lượng cao 1 TB thoải m&aacute;i trong việc lưu trữ v&agrave; ph&ugrave; hợp với sinh vi&ecirc;n trong nhu cầu học tập v&agrave; l&agrave;m việc, giải tr&iacute; cơ bản.<br />\r\nViệc trang bị card đồ hoạ rời Nvidia GeForce MX130 sẽ gi&uacute;p m&aacute;y c&oacute; thể chơi được c&aacute;c game đồ hoạ nặng hiện nay như PUBG, The Witcher 3, Assassin&rsquo;s Creed Origins ở c&aacute;c mức thiết lập low đến all low với 30 fps+ khung h&igrave;nh. Tuy nhi&ecirc;n nếu l&agrave;m đồ họa render th&igrave; m&aacute;y sẽ kh&oacute; &ldquo;kham nổi&rdquo;, card MX130 chưa đủ mạnh mẽ để l&agrave;m những c&ocirc;ng việc nặng nhọc như vậy n&ecirc;n m&igrave;nh khuy&ecirc;n nếu bạn l&agrave; d&acirc;n chuy&ecirc;n đồ hoạ th&igrave; bạn n&ecirc;n t&igrave;m đến những d&ograve;ng m&aacute;y cao cấp hơn, v&iacute; dụ như Laptop Acer Predator Helios 300<br />\r\nNhưng Asus Vivobook X507UF c&oacute; 1 ưu điểm đ&oacute; l&agrave; ho&agrave;n to&agrave;n c&oacute; thể n&acirc;ng cấp được cho c&aacute;c nhu cầu l&agrave;m việc cao hơn, Ram c&oacute; thể n&acirc;ng tối đa l&agrave; 16Gb, m&aacute;y cũng hỗ trợ khe M.2 SATA để lắp ổ SSD gi&uacute;p tăng tốc độ đọc ghi.<br />\r\n<br />\r\n<strong>Tản nhiệt</strong></p>\r\n\r\n<p><strong><img alt=\"Laptop Asus Vivobook X507UF- EJ077T\" src=\"https://laptop88.vn/media/lib/4163_4dd-1.jpg\" style=\"height:594px; width:1000px\" /></strong></p>\r\n\r\n<p>Asus sử dụng C&ocirc;ng nghệ mang t&ecirc;n ASUS IceCool c&oacute; thiết kế bộ điều khiển quạt th&ocirc;ng minh, ống dẫn nhiệt mỏng v&agrave; tinh tế để tạo n&ecirc;n hệ thống tản nhiệt hiệu quả gi&uacute;p duy tr&igrave; nhiệt độ trung b&igrave;nh tr&ecirc;n bề mặt k&ecirc; l&ograve;ng b&agrave;n tay chỉ dưới 36&deg;C &mdash; thấp hơn nhiệt độ cơ thể. Nhờ đ&oacute; bạn c&oacute; thể sử dụng m&aacute;y l&acirc;u m&agrave; nhiệt độ cũng chỉ ở mức ấm chứ chưa thể gọi l&agrave; n&oacute;ng.</p>\r\n\r\n<p><strong>Thời lượng pin</strong><br />\r\nChiếc Asus X507UF n&agrave;y được trang bị vi&ecirc;n pin c&oacute; dung lượng 3 cell sẽ cho thời gian sử dụng m&aacute;y li&ecirc;n tục từ 3-5 giờ đồng hồ. Nếu chỉ sử dụng để lướt web, xem phim, l&agrave;m việc văn ph&ograve;ng như Word, Excel, th&igrave; chiếc Laptop n&agrave;y c&oacute; thể hoạt động li&ecirc;n tục từ 4 giờ trở l&ecirc;n, c&ograve;n nếu chơi game giải tr&iacute; hay xem phim li&ecirc;n tục th&igrave; chỉ khoảng 3 giờ trở lại th&ocirc;i nh&eacute;.<br />\r\nAsus Vivobook X507UF c&ograve;n t&iacute;ch hợp c&ocirc;ng nghệ sạc pin nhanh đến 60% trong v&ograve;ng 49 ph&uacute;t v&agrave; c&ocirc;ng nghệ ASUS SuperBatt đảm bảo tuổi thọ của pin hơn gấp 3 lần so với c&aacute;c loại pin thường kh&aacute;c.<br />\r\n<br />\r\n<strong>Kết luận</strong><br />\r\nQua b&agrave;i đ&aacute;nh gi&aacute; ở tr&ecirc;n, c&aacute; nh&acirc;n người viết cho rằng Asus Vivobook X507UF (i7-8550U) l&agrave; chiếc laptop tầm trung c&oacute; mức gi&aacute; tốt, thiết kế gọn nhẹ, rất ph&ugrave; hợp với c&aacute;c bạn sinh vi&ecirc;n hoặc những người d&ugrave;ng văn ph&ograve;ng nhu cầu kh&ocirc;ng qu&aacute; nặng nề về đồ hoạ hay chơi game. ASUS đ&atilde; l&agrave;m rất tốt trong việc thiết kế 1 sản phẩm &ldquo;Ngon, Bổ, Rẻ&rdquo; như vậy trong tầm gi&aacute;.</p>\r\n\r\n<p><em>Ưu điểm&nbsp;</em><br />\r\n-Thiết kế kh&aacute; tốt,<br />\r\n- K&iacute;ch thước gọn nhẹ&nbsp;<br />\r\n- C&oacute; thể n&acirc;ng cấp RAM v&agrave; SSD để tăng hiệu năng<br />\r\n- Trang bị c&ocirc;ng nghệ sạc nhanh&nbsp;<br />\r\n- Hỗ trợ đầy đủ c&aacute;c cổng kết nối phổ th&ocirc;ng&nbsp;<br />\r\n- M&agrave;n h&igrave;nh hiển thị đẹp&nbsp;<br />\r\n- Tản nhiệt tốt</p>\r\n\r\n<p><em>Nhược điểm</em></p>\r\n\r\n<p>- Thời lượng pin thấp<br />\r\n- RAM &iacute;t&nbsp;</p>', 'AsusVivoBook.png', 3, '2019-05-21 07:03:58', '2019-05-21 07:03:58');
INSERT INTO `products` (`product_id`, `name`, `slug`, `cpu`, `ram`, `storage`, `display`, `vga`, `battery`, `weight`, `material`, `kind`, `condition`, `price`, `promotion`, `status`, `featured`, `warranty`, `description`, `img`, `category`, `created_at`, `updated_at`) VALUES
(6, 'Lenovo Thinkpad T480', 'lenovo-thinkpad-t480', 'Intel Core i7 8550U', '16GB DDR4', 'SSD NVMe 512GB', '14 Inch Full HD', 'Intel HD Graphics 620', '3 cells - dual battery (24 + 24 Wh)', '1.58 Kg', 'Nhựa bền và Hợp kim Magie', 'IT Lập trình', 'New', 25500000, 'Balo chống sốc cao cấp + Chuột không dây + Bàn di chuột. Trị giá: 400.000đ', 1, 1, '24 tháng', '<p><strong>Tiệm cận SỰ HO&Agrave;N HẢO của Laptop Doanh nh&acirc;n - ThinkPad T480</strong></p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_01_06_21.Still003.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Khi x&eacute;t tr&ecirc;n kh&iacute;a cạnh trải nghiệm người d&ugrave;ng, những chiếc Laptop Lenovo Thinkpad lu&ocirc;n được đ&aacute;nh gi&aacute; rất cao, đặc biệt l&agrave; những d&ograve;ng m&aacute;y từ tầm trung trở l&ecirc;n. Ti&ecirc;u biểu v&agrave; được rất nhiều người d&ugrave;ng ch&uacute; &yacute;, ưa chuộng l&agrave; ThinkPad d&ograve;ng T với đặc th&ugrave; l&agrave; Series T400 với k&iacute;ch thước 14&rdquo; - một k&iacute;ch thước cực kỳ l&yacute; tưởng v&agrave; ph&ugrave; hợp cho mọi c&ocirc;ng việc t&aacute;c vụ của người d&ugrave;ng.</p>\r\n\r\n<p>Qua b&agrave;i viết n&agrave;y, Laptop88 sẽ giới thiệu tới c&aacute;c bạn một lựa chọn rất hấp dẫn trong d&ograve;ng ThinkPad Series T400. Đ&oacute; ch&iacute;nh l&agrave; Lenovo ThinkPad T480 - một chiếc <a href=\"https://laptop88.vn/laptop/c2.html?filter=%2C537%2C\">Laptop Doanh Nh&acirc;n</a> tiệm cận SỰ HO&Agrave;N HẢO!</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_00_37_09.Still001.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p><strong>Cấu h&igrave;nh đ&aacute;nh gi&aacute;:</strong></p>\r\n\r\n<p>CPU: Intel Core i7-8550U (4 nh&acirc;n 8 luồng) 1,80GHz</p>\r\n\r\n<p>Card đồ họa: Intel UHD Graphic 620</p>\r\n\r\n<p>RAM: 16GB DDR4</p>\r\n\r\n<p>M&agrave;n h&igrave;nh: 14 inch 16:9 Full HD IPS</p>\r\n\r\n<p>Ổ cứng: 500 SSD NVMe</p>\r\n\r\n<p>Cổng giao tiếp: 2 x USB 3.1 Type-C (1 Thunderbolt 3), 2 x USB 3.0 Type-A, 1 x HDMI, 1 x LAN, 1 x Jack Audio Combo 3.5</p>\r\n\r\n<p>Webcam: Think Shutter</p>\r\n\r\n<p>Pin: 48Wh gắn liền v&agrave; 72Wh gắn ngo&agrave;i.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Thiết kế:</strong></p>\r\n\r\n<p>Thiết kế của chiếc T480 kh&ocirc;ng c&oacute; bất cứ thay đổi n&agrave;o v&agrave; giữ nguy&ecirc;n tất cả những n&eacute;t &ldquo;nổi bật&rdquo; của d&ograve;ng Lenovo ThinkPad. Tổng thể của chiếc laptop n&agrave;y vẫn mang phong c&aacute;ch cao cấp, giản dị với một m&agrave;u đen tuyền, v&ocirc; c&ugrave;ng liền lạc. Nếu đ&atilde; lỡ một lần th&iacute;ch phong c&aacute;ch thiết kế n&agrave;y của Laptop ThinkPad chắc chắn người d&ugrave;ng sẽ kh&oacute; c&oacute; thể từ bỏ được n&oacute;. Logo ThinkPad quen thuộc được bố tr&iacute; ở g&oacute;c tr&ecirc;n b&ecirc;n tr&aacute;i của nắp m&aacute;y v&agrave; dấu chấm hiển thị đ&egrave;n s&aacute;ng th&ocirc;ng b&aacute;o t&igrave;nh trạng m&aacute;y.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_01_04_11.Still002.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>T480 được thiết kế bền bỉ cứng c&aacute;p đ&aacute;p ứng ti&ecirc;u chuẩn độ bền qu&acirc;n sự MIL-STD-810G. Phần vỏ nắp m&aacute;y được l&agrave;m bằng <em>vật liệu Polyphenylene Sulfide (PPS)</em> v&agrave; bề mặt được phủ một lớp cao su mỏng (soft-touch), đặc t&iacute;nh của PPS kh&aacute; giống với magnesium với mật độ, khả năng chịu nhiệt v&agrave; chịu lực vặn xoắn, va đập rất tốt. Bề mặt của T480 với lớp phủ cao su hơi sần, nh&aacute;m tay khi vuốt. C&ograve;n lại ri&ecirc;ng phần th&acirc;n m&aacute;y gồm nội thất, đ&aacute;y m&aacute;y được l&agrave;m bằng<em> nhựa gia cường bằng sợi thủy tinh</em> để tăng sự chắc chắn cho th&acirc;n m&aacute;y.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_01_06_21.Still003.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Ngo&agrave;i ra, T480 cũng sở hữu một k&iacute;ch thước rất thon gọn l&agrave; 337 x 233 x 20 mm c&ugrave;ng c&acirc;n nặng chỉ 1,63Kg. Với một chiếc laptop sở hữu m&agrave;n h&igrave;nh 14 inch, ThinkPad T480 rất tiện lợi v&agrave; gi&uacute;p người d&ugrave;ng dễ d&agrave;ng mang đi mọi nơi phục vụ c&ocirc;ng việc, học tập hay giải tr&iacute; đơn giản.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh:</strong></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của T480 c&oacute; m&atilde; LP140QH2-SPB1 do LG Philips sản xuất với k&iacute;ch thước m&agrave;n h&igrave;nh 14 inch trang bị &nbsp;c&ocirc;ng nghệ IPS, độ ph&acirc;n giải FullHD, tỉ lệ 16:9, mật độ điểm ảnh khoảng 210 ppi</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_01_30_06.Still004.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Chất lượng hiển của m&agrave;n h&igrave;nh n&agrave;y ở mức kh&aacute; với độ bao phủ c&aacute;c dải m&agrave;u rộng hơn so với T480s, đạt 75% Adobe RGB, 69% NTSC v&agrave; 93% sRGB. Tuy nhi&ecirc;n, Delta-E lại kh&ocirc;ng tốt với tỉ lệ trung b&igrave;nh 3.3, cao nhất 7.3 v&agrave; nhiều m&agrave;u sắc bị sai như xanh, đỏ v&agrave; v&agrave;ng. Ngo&agrave;i ra độ s&aacute;ng trung b&igrave;nh của m&agrave;n l&agrave; 220 nits</p>\r\n\r\n<p>Nh&igrave;n chung chất lượng h&igrave;nh ảnh từ m&agrave;n h&igrave;nh của ThinkPad T480 ở mức tốt. N&oacute; ph&ugrave; hợp với c&aacute;c t&aacute;c văn ph&ograve;ng, những c&ocirc;ng việc kh&ocirc;ng cần sự ch&iacute;nh x&aacute;c cao về m&agrave;u sắc. Nhưng với một m&agrave;u sắc c&oacute; phần rực rỡ, sinh động tuy độ ch&iacute;nh x&aacute;c kh&ocirc;ng cao, chiếc laptop n&agrave;y vẫn sẽ phục vụ rất tốt nhu cầu giải tr&iacute;, xem video, ca nhạc, &hellip; của người d&ugrave;ng.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_02_01_12.Still005.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Bản lề của T480 được l&agrave;m ch&igrave;m cho g&oacute;c mở m&agrave;n h&igrave;nh tối đa 180 độ. Nhưng nếu so với c&aacute;c d&ograve;ng thinkPad T trước th&igrave; b&agrave;n lề của m&aacute;y đ&atilde; nhỏ hơn nhiều. Ngo&agrave;i ra với một viền m&agrave;n h&igrave;nh d&agrave;y 12mm tuy chưa thể gọi l&agrave; mỏng khi so s&aacute;nh với c&aacute;c Ultrabook hay laptop doanh nh&acirc;n tr&ecirc;n thị trường nhưng n&oacute; lại rất ph&ugrave; hợp với tổng thể của chiếc m&aacute;y. Viền m&agrave;n h&igrave;nh kiểu n&agrave;y vừa l&agrave;m tăng tỷ lệ m&agrave;n h&igrave;nh m&agrave; lại vẫn giữ được n&eacute;t chắc chắn, bền bỉ của T480.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Cổng kết nối:</strong></p>\r\n\r\n<p>D&ugrave; l&agrave; một chiếc laptop doanh nh&acirc;n, nhưng khi nh&igrave;n ra xung quanh T480 vẫn c&oacute; đủ c&aacute;c cổng kết nối để đảm bảo c&ocirc;ng việc của người d&ugrave;ng. Tuy vậy, việc bố tr&iacute; c&aacute;c cổng kết nối sang cạnh b&ecirc;n phải chiếc laptop n&agrave;y sẽ g&acirc;y ảnh hưởng kh&aacute; nhiều đến những người d&ugrave;ng mới bắt đầu l&agrave;m quen với Thinkpad hoặc quen sử dụng chuột gắn ngo&agrave;i, khi ch&uacute;ng chiếm một khoảng kh&ocirc;ng gian tương đối lớn khi cắm c&aacute;c d&acirc;y kết nối.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_02_32_11.Still006.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Cạnh tr&aacute;i m&aacute;y c&oacute; 2 cổng USB 3.1 Type-C, cả hay cổng n&agrave;y đều được t&iacute;ch hợp t&iacute;nh năng sạc nhưng chỉ c&oacute; ri&ecirc;ng một cổng được trang bị Thunderbolt 3.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_02_40_04.Still007.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Cạnh phải m&aacute;y người d&ugrave;ng sẽ c&oacute; một kh&oacute;a Kensington, 1 đầu đọc thẻ SD, 1 cổng mạng LAN, 2 cổng USB 3.0 Type-A, 1 cổng HDMI v&agrave; 1 Jack Audio Combo 3.5.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>B&agrave;n ph&iacute;m, Touchpad v&agrave; Trackpoint</strong></p>\r\n\r\n<p>Trải nghiệm b&agrave;n ph&iacute;m của T480 giống hệt so với những chiếc ThinkPad cao cấp tiền nhiệm. Layout hầu như kh&ocirc;ng c&oacute; sự thay đổi g&igrave; với h&agrave;nh tr&igrave;nh ph&iacute;m 1,5mm, c&aacute;c ph&iacute;m k&iacute;ch thước ti&ecirc;u chuẩn (15 x 15 mm), c&oacute; độ nảy cao, bề mặt được l&agrave;m cong, hơi l&otilde;m v&agrave; được phủ một lớp nh&aacute;m nhẹ, cảm gi&aacute;c khi g&otilde; rất b&aacute;m tay.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, khi mua chiếc laptop n&agrave;y người d&ugrave;ng sẽ cần ch&uacute; &yacute; rằng T480 c&oacute; nhiều phi&ecirc;n bản phục vụ cho c&aacute;c khu vực, quốc gia kh&aacute;c nhau, n&ecirc;n vị tr&iacute; c&aacute;c n&uacute;t tr&ecirc;n b&agrave;n ph&iacute;m c&oacute; thể bị thay đổi cho ph&ugrave; hợp với ng&ocirc;n ngữ ở địa điểm đ&oacute;. Việc thay đổi c&agrave;i đặt layout b&agrave;n ph&iacute;m n&agrave;y c&oacute; thể thực hiện một c&aacute;ch dễ d&agrave;ng trong c&agrave;i đặt của Window, hoặc để c&oacute; thể trải nghiệm tốt nhất người d&ugrave;ng c&oacute; thể d&aacute;n đ&egrave; t&ecirc;n c&aacute;c n&uacute;t mặc định để tr&aacute;nh g&otilde; sai n&uacute;t. Kh&aacute;ch h&agrave;ng mua m&aacute;y tại Laptop88 sẽ được hỗ trợ HO&Agrave;N TO&Agrave;N MIỄN PH&Iacute; thao t&aacute;c n&agrave;y.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_03_05_20.Still008.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Touchpad của m&aacute;y được trang bị bề mặt phủ k&iacute;nh, hỗ trợ đa điểm với driver Microsoft Precision Touchpad cho độ trễ thao t&aacute;c thấp v&agrave; 2 ph&iacute;m chuột t&iacute;ch hợp b&ecirc;n dưới. Trải nghiệm sử dụng v&agrave; điều hướng tr&ecirc;n Touchpad l&agrave; rất tốt, kh&ocirc;ng hề c&oacute; cảm gi&aacute;c bị r&iacute;t tay v&agrave; rất nhạy. Hai ph&iacute;m bấm chuột c&oacute; độ nảy cao nhưng c&oacute; cảm gi&aacute;c hơi cứng, người d&ugrave;ng sẽ mất một thời gian để l&agrave;m quen.</p>\r\n\r\n<p>Trackpoint của T480 cho một độ nhạy tốt, cảm gi&aacute;c di chuyển dễ d&agrave;ng. Đ&acirc;y vẫn l&agrave; một &ldquo;đặc sản&rdquo; của Lenovo tr&ecirc;n những chiếc ThinkPad m&agrave; nếu người d&ugrave;ng đ&atilde; quen sử dụng sẽ kh&ocirc;ng c&ograve;n cần phải phụ thuộc v&agrave;o con chuột gắn ngo&agrave;i nữa, tăng sự di động của chiếc laptop l&ecirc;n rất nhiều.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_03_47_04.Still009.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>B&ecirc;n cạnh b&agrave;n ph&iacute;m, T480 cũng được Lenovo trang bị một cảm biến v&acirc;n tay ti&ecirc;u chuẩn theo cấu h&igrave;nh. Tuy vậy với thiết kế kh&aacute; nhỏ v&agrave; đặt s&acirc;u dưới vỏ m&aacute;y, cảm biến n&agrave;y kh&ocirc;ng được nhạy v&agrave; người d&ugrave;ng sẽ phải giữ ng&oacute;n tay tr&ecirc;n đ&oacute; một l&uacute;c m&aacute;y mới nhận v&agrave; mở kh&oacute;a.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Hiệu năng:</strong></p>\r\n\r\n<p>Lenovo ThinkPad T480 c&oacute; nhiều t&ugrave;y chọn cấu h&igrave;nh, phi&ecirc;n bản Laptop88 đang đ&aacute;nh gi&aacute; l&agrave; phi&ecirc;n bản cơ bản nhất. M&aacute;y đang trang bị vi xử l&yacute; thể hệ thứ 8 Core i7- 8550U c&ugrave;ng 16GB RAM DDR4 v&agrave; ổ cứng SSD NVMe PM981 500GB đến từ thương hiệu Samsung. C&oacute; thể n&oacute;i l&agrave; kh&ocirc;ng c&oacute; bất kỳ một t&aacute;c vụ văn ph&ograve;ng n&agrave;o c&oacute; thể l&agrave;m kh&oacute; được chiếc laptop n&agrave;y. Bạn c&oacute; thể ngồi h&agrave;ng giờ, bật h&agrave;ng chục tab tr&igrave;nh duyệt, T480 vẫn sẽ bền bỉ c&ugrave;ng bạn v&agrave; kh&ocirc;ng xảy ra bất cứ hiện tượng giật lag n&agrave;o.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_04_01_03.Still010.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Về hiệu năng thực tế, T480 đặt số điểm benchmark rất tốt với b&agrave;i test PCMark 10 khi đạt 3865 kh&ocirc;ng thua k&eacute;m g&igrave; so với c&aacute;c Ultrabook, hay laptop doanh nh&acirc;n c&ugrave;ng tầm gi&aacute;. Ngo&agrave;i ra với Card đồ họa t&iacute;ch hợp Intel UHD Graphic 620, m&aacute;y cũng thể kh&aacute; tốt qua c&aacute;c b&agrave;i test 3DMark</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_04_30_08.Still011.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">&nbsp;\r\n			<p><strong>B&agrave;i test</strong></p>\r\n			</td>\r\n			<td>&nbsp;\r\n			<p><strong>ThinkPad T480</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p>3DMark</p>\r\n			</td>\r\n			<td>\r\n			<p>Fire Strike</p>\r\n			</td>\r\n			<td>\r\n			<p>987</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Time Spy</p>\r\n			</td>\r\n			<td>\r\n			<p>405</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Night Raid</p>\r\n			</td>\r\n			<td>\r\n			<p>4783</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Sky Driver</p>\r\n			</td>\r\n			<td>\r\n			<p>4090</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; khi thử nghiệm chơi, T480 vẫn cho một mức FPS rất tốt với tựa Li&ecirc;n minh Huyền thoại với trung b&igrave;nh 80 FPS ở mức thiết lập đồ họa Rất Cao. Nhưng khi chuyển sang tựa game y&ecirc;u cầu cấu h&igrave;nh mạnh hơn như Fifa Online, T480 thể hiện r&otilde; rằng n&oacute; l&agrave; một chiếc laptop phục vụ chủ yếu cho c&ocirc;ng việc khi mức FPS chỉ đạt được trung b&igrave;nh l&agrave; 20 ở mức thiết lập thấp nhất v&agrave; bề mặt m&aacute;y n&oacute;ng l&ecirc;n r&otilde; rệt.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_04_48_11.Still012.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>C&oacute; một điều th&uacute; vị kh&aacute;c về chiếc laptop n&agrave;y l&agrave; chiếc ổ cứng Samsung PM981 tr&ecirc;n T480 - đ&aacute;ng ra phải đặt được tốc độ đọc/ ghi cao hơn cũng như ngang ngửa với những chiếc laptop c&ugrave;ng d&ugrave;ng d&ograve;ng ổ n&agrave;y, điển h&igrave;nh như chiếc Yoga 920 với tốc độ đọc tr&ecirc;n 3000MB/s v&agrave; ghi gần 2000MB/s. Nhưng PM981 tr&ecirc;n T480 lại chỉ đạt hiệu năng bằng một nửa. L&yacute; do l&agrave; cho d&ugrave; ổ cứng SSD NVMe n&agrave;y sử dụng giao tiếp PCIe 3.0 nhưng n&oacute; lại chỉ khai th&aacute;c v&agrave; d&ugrave;ng 2 lane PCIe 3.0 thay v&igrave; 4 lane do giới hạn của adapter trong khay ổ 2.5inch. V&igrave; nguy&ecirc;n nh&acirc;n n&agrave;y ổ cứng SSD bị giới hạn băng th&ocirc;ng chỉ v&agrave;o khoảng 1700MB/s đọc v&agrave; 1650MB/s ghi.</p>\r\n\r\n<p><strong><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_05_19_09.Still013.jpg\" style=\"height:1080px; width:1920px\" /></strong></strong></p>\r\n\r\n<p>Đối với RAM, T480 được gắn sẵn 2 thanh ram 8GB DDR4 chạy Dual - Channel, người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể n&acirc;ng cấp l&ecirc;n 32GB để c&oacute; một trải nghiệm tuyệt vời hơn.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Loa:</strong></p>\r\n\r\n<p>Chất lượng &acirc;m thanh của chiếc T480 n&agrave;y kh&aacute; tốt, &acirc;m lượng lớn, r&otilde; r&agrave;ng nhưng m&aacute;y vẫn c&oacute; một điểm yếu cố hữu tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y doanh nh&acirc;n, văn ph&ograve;ng l&agrave; chỉ c&oacute; thể thể hiện được dải mid rất tốt, treble cao nhưng bass th&igrave; kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o. N&oacute; sẽ chỉ ph&ugrave; hợp với nhu cầu hội thoại video với c&aacute;c giải ph&aacute;p nội bộ hay Skype for Business .</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Webcam:</strong></p>\r\n\r\n<p><strong><img alt=\"Đánh giá chi tiết Lenovo ThinkPad T480\" src=\"https://laptop88.vn/media/lib/4297_T480.00_06_01_12.Still014.jpg\" style=\"height:1080px; width:1920px\" /></strong></p>\r\n\r\n<p>Chiếc laptop n&agrave;y được Lenovo trang bị một cơ chế Webcam rất th&uacute; vị được gọi l&agrave; ThinkShutter. Thực chất đ&acirc;y l&agrave; một chiếc webcam c&oacute; cửa đ&oacute;ng mở để bảo mật, khi cần sử dụng người d&ugrave;ng c&oacute; thể gạt chốt để lộ webcam ra, khi kh&ocirc;ng sử dụng cửa n&agrave;y đ&oacute;ng lại sẽ để lọ cảm biến của m&aacute;y. Thiết kế n&agrave;y nhằm gi&uacute;p người d&ugrave;ng bảo mật tốt hơn trước những lo ngại về việc webcam bị hack.</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>\r\n\r\n<p><strong>Tổng kết:</strong></p>\r\n\r\n<p>Với bỏ ra hơn 25 triệu d&agrave;nh cho Lenovo ThinkPad T480, chiếc laptop n&agrave;y sẽ đem lại cho bạn một trải nghiệm cực kỳ tuyệt vời với với thiết kế chất lượng cao cấp, phong c&aacute;ch giản dị nhưng v&ocirc; c&ugrave;ng mạnh mẽ, bền bỉ, b&agrave;n ph&iacute;m tuyệt vời, h&igrave;nh ảnh m&agrave;n h&igrave;nh hiển thị tốt, hiệu năng đảm bảo phục vụ mọi nhu cầu văn ph&ograve;ng. Nếu như bạn l&agrave; một người l&agrave;m văn ph&ograve;ng lu&ocirc;n phải sử dụng m&aacute;y t&iacute;nh, laptop li&ecirc;n tục h&agrave;ng giờ liền hay c&oacute; những c&ocirc;ng việc phải thường xuy&ecirc;n di chuyển, T480 chắc chắn sẽ l&agrave; một lựa chọn kh&ocirc;ng n&ecirc;n bỏ qua trong ph&acirc;n kh&uacute;c laptop doanh nh&acirc;n tầm trung.</p>', 'ThinkpadT480.png', 4, '2019-05-21 07:18:44', '2019-05-21 07:18:44'),
(7, 'Macbook Pro Touch 13.3 2018', 'macbook-pro-touch-133-2018', 'Intel Core i5 Kabylake Refresh', '8 GB DDR3L 2133 MHz (On board)', 'SSD 256GB', '13.3 Inch Retina (2560 x 1600) IPS, LED Backlit', 'Intel Iris Plus Graphics 655', 'lithium-polymer  58Wh', '1.37 Kg', 'Kim loại nguyên khối', 'Giải trí - Văn phòng', 'New', 44500000, '44000000', 1, 1, '24 tháng', '<p>Apple đ&atilde; cho ra mắt phi&ecirc;n bản Macbook Pro 2018 vẫn l&agrave; vẻ ngo&agrave;i sang trọng, thiết kế Unibody quen thuộc của&nbsp;d&ograve;ng&nbsp; m&aacute;y t&iacute;nh x&aacute;ch tay&nbsp;cao cấp đến từ thương hiệu &quot;T&aacute;o Khuyết&quot;. Sự kh&aacute;c biệt của Macbook Pro Touch so với phi&ecirc;n bản cũ phải n&oacute;i đến sự n&acirc;ng cấp cũng như c&oacute; nhiều cải tiến mới mẻ đến từ b&ecirc;n trong với một cấu h&igrave;nh mạnh mẽ hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế vỏ nh&ocirc;m nguy&ecirc;n khối cao cấp</h3>\r\n\r\n<p>L&agrave; một chiếc m&aacute;y t&iacute;nh thuộc d&ograve;ng sản phẩm cao đến từ&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\">Apple</a>,&nbsp;Macbook Pro 13 inch 2018 được trang bị lớp vỏ nh&ocirc;m nguy&ecirc;n khối&nbsp;Unibody sang trọng, tinh tế v&agrave; chắc chắn, b&ecirc;n cạnh đ&oacute; m&aacute;y với trọng lượng chỉ 1.37 kg, người d&ugrave;ng c&oacute; thể mang m&aacute;y theo sử dụng mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế cao cấp trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-9.jpg\" /></a></p>\r\n\r\n<h3>Xử l&yacute; đồ họa - kĩ thuật chuy&ecirc;n nghiệp</h3>\r\n\r\n<p>Apple Macbook Pro 2018 13 inch được trang bị một cấu h&igrave;nh mạnh mẽ với vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cpu-intel-kabylake-refresh-la-gi-1017640\" target=\"_blank\">Core i5&nbsp;</a>tốc độ 2.3 GHz,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ram-ddr3l-on-board-la-nhu-the-nao-951049\" target=\"_blank\">8 GB RAM</a>&nbsp;v&agrave; bộ nhớ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/o-cung-ssd-la-gi-923073\" target=\"_blank\">SSD&nbsp;256 GB</a>&nbsp;cho hiệu năng hoạt động mượt m&agrave; khi chạy đa nhiệm c&aacute;c t&aacute;c vụ th&ocirc;ng thường đến xử l&yacute; đồ họa - kĩ thuật phức tạp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-11.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA\" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-11.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Retina si&ecirc;u sắc n&eacute;t</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\">Retina</a>&nbsp;si&ecirc;u sắc n&eacute;t độc quyền của Apple với độ ph&acirc;n giải (2560 x 1600) cho trải nghiệm h&igrave;nh ảnh sống động, ch&acirc;n thật, rực rỡ, m&agrave;u sắc h&igrave;nh ảnh hiển thị với độ tương phản cao l&agrave; sự lựa chọn ph&ugrave; hợp với người d&ugrave;ng chuy&ecirc;n về thiết kế đồ họa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-display.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Retina sắc nét của Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-display.jpg\" /></a></p>\r\n\r\n<h3>Cổng&nbsp;Thunderbolt hiện đại</h3>\r\n\r\n<p>Macbook Pro Touchbar 2018 13 inch được Apple trang bị l&ecirc;n đến 4 cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/thunderbolt-3-usbc-la-gi-959274\" target=\"_blank\">Thunderbolt 3 (USB-C)</a>&nbsp;cho ph&eacute;p truyền dữ liệu l&ecirc;n tới&nbsp;40Gbps, cao gấp đ&ocirc;i so với Thunderbolt 2 chỉ&nbsp;20Gbps&nbsp;trong khi điện năng ti&ecirc;u thụ th&igrave; chỉ bằng ph&acirc;n nửa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-4.jpg\" onclick=\"return false;\"><img alt=\"Cổng thunderbolt 3 trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-4.jpg\" /></a></p>\r\n\r\n<h3>B&agrave;n ph&iacute;m với đ&egrave;n nền tiện lợi</h3>\r\n\r\n<p>Với cơ chế b&agrave;n ph&iacute;m&nbsp;bướm thế hệ thứ ba kh&aacute;c biệt với c&aacute;c phi&ecirc;n bản kh&aacute;c, Macbook Pro 2018 cho khả năng g&otilde; linh hoạt v&agrave; y&ecirc;n tĩnh hơn. C&ugrave;ng với đ&egrave;n nền được trang bị tr&ecirc;n b&agrave;n ph&iacute;m hỗ trợ người d&ugrave;ng l&agrave;m việc thoải m&aacute;i, dễ d&agrave;ng thao t&aacute;c hơn v&agrave;o ban đ&ecirc;m cũng như đặc th&ugrave; c&ocirc;ng việc ở nơi c&oacute; điều kiện thiếu &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-baitin2.jpg\" onclick=\"return false;\"><img alt=\"Bàn nền phím trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-baitin2.jpg\" /></a></p>\r\n\r\n<h3>Touch bar v&agrave; cảm biến Touch ID tiện &iacute;ch</h3>\r\n\r\n<p>Giống như phi&ecirc;n bản Macbook Pro 2017,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/touch-bar-tren-macbook-la-gi-956456\" target=\"_blank\">Touchbar</a>&nbsp;được đặt ph&iacute;a tr&ecirc;n b&agrave;n ph&iacute;m v&agrave; thay thế c&aacute;c ph&iacute;m chức năng ở h&agrave;ng đầu của b&agrave;n ph&iacute;m.&nbsp;T&ugrave;y v&agrave;o ứng dụng v&agrave; c&ocirc;ng việc m&agrave; bạn đang thao t&aacute;c tr&ecirc;n&nbsp;Macbook&nbsp;m&agrave;&nbsp;Touchbar&nbsp;sẽ hiển thị c&aacute;c ph&iacute;m tắt kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-6.jpg\" onclick=\"return false;\"><img alt=\"Toucj ID trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-6.jpg\" /></a></p>\r\n\r\n<p>Cảm biến v&acirc;n tay Touch ID&nbsp;cho ph&eacute;p người d&ugrave;ng truy cập nhanh, đăng nhập nhanh, dữ liệu lưu trữ tr&ecirc;n m&aacute;y cũng được bảo mật tối ưu hơn.</p>\r\n\r\n<h3>&Acirc;m thanh sống động</h3>\r\n\r\n<p>Loa tr&ecirc;n MacBook Pro 2018 13 đ&atilde; được căn chỉnh v&agrave; thay đổi để dải đ&aacute;p ứng rộng hơn, bass mạnh hơn cho chất lượng cao hơn, c&acirc;n bằng v&agrave; chi tiết hơn. Đ&aacute;p ứng mọi nhu cầu l&agrave;m việc cũng như giải tr&iacute; thư gi&atilde;n của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-7.jpg\" onclick=\"return false;\"><img alt=\"Hệ thống loa trên Apple Macbook Pro 2018 13 inch Touchbar MR9Q2SA \" src=\"https://cdn.tgdd.vn/Products/Images/44/184384/apple-macbook-pro-touch-mr9q2sa-a-2018-7.jpg\" /></a></p>', 'MacBookPro13.3-2018.jpg', 5, '2019-05-21 10:41:42', '2019-06-02 17:51:36'),
(9, 'HP Pavilion Gaming 15 2018', 'hp-pavilion-gaming-15-2018', 'Intel Core i5 8300H', '8GB DDR4 2666MHz', '1TB HDD (7200 rpm)', '15.6 Inch FHD IPS', 'Nvidia GeForce GTX 1050 4GB', '3 cell 52 Wh Pin liền', '2.2 Kg', 'Nhựa', 'Gaming', 'New', 24000000, 'Tặng Balo Laptop và PMH 3,000,000đ', 1, 1, '24 tháng', '<h3>Đ&aacute;nh gi&aacute; chi tiết HP Pavilion Power 15-cx0179TX/i5-8300H</h3>\r\n\r\n<p>HP Pavilion Power 15-cx0179TX/i5-8300H l&agrave; chiếc laptop th&uacute; vị d&agrave;nh cho c&aacute;c game thủ. N&oacute; đến với chip xử l&yacute; mạnh mẽ Intel thế hệ thứ 8, đi k&egrave;m card đồ họa rời NVIDIA GeForce GTX 1050Ti v&agrave; mức gi&aacute; kh&ocirc;ng qu&aacute; cao.</p>\r\n\r\n<h3><br />\r\n<strong>Thiết kế t&aacute;o bạo, nổi bật</strong></h3>\r\n\r\n<p><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-01.jpg\" style=\"height:560px; width:840px\" /></p>\r\n\r\n<p>So với thế hệ trước, HP Pavilion Power 15-cx0179TX/i5-8300H sở hữu nhiều cải tiến trong thiết kế. Bạn sẽ thấy chiếc m&aacute;y t&iacute;nh chơi game n&agrave;y c&oacute; ngoại h&igrave;nh nổi bật v&agrave; t&aacute;o bạo hơn rất nhiều. Với &aacute;nh s&aacute;ng đ&egrave;n LED ri&ecirc;ng biệt v&agrave; c&aacute;c điểm nhấn ph&ugrave; hợp, m&aacute;y t&iacute;nh x&aacute;ch tay n&agrave;y tạo ra một sự hấp dẫn đặc biệt đối với mỗi ai khi nh&igrave;n v&agrave;o. Lựa chọn phối m&agrave;u xanh v&agrave; đen cũng rất th&uacute; vị thay v&igrave; như những laptop gaming kh&aacute;c l&agrave; đỏ đen thường thấy. B&ecirc;n cạnh đ&oacute;, HP Pavilion Power 15 c&oacute; trọng lượng chỉ 2.2kg n&ecirc;n bạn c&oacute; thể dễ d&agrave;ng mang n&oacute; đi bất cứ đ&acirc;u trong ba l&ocirc; để sử dụng.</p>\r\n\r\n<h3><br />\r\n<strong>M&agrave;n h&igrave;nh tuyệt đẹp</strong></h3>\r\n\r\n<p><strong><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-02.jpg\" style=\"height:560px; width:840px\" /></strong></p>\r\n\r\n<p>Một trong c&aacute;c điểm nhấn tr&ecirc;n HP Pavilion Power 15-cx0179TX/i5-8300H ch&iacute;nh l&agrave; m&agrave;n h&igrave;nh. Bạn sẽ h&agrave;i l&ograve;ng về mọi thứ tr&ecirc;n m&agrave;n h&igrave;nh 15.6 inch với viền bezel hẹp v&agrave; độ ph&acirc;n giải cao Full HD. Với c&ocirc;ng nghệ hiển thị UWVA mang tới g&oacute;c nh&igrave;n rộng, m&agrave;u sắc tươi s&aacute;ng v&agrave; độ s&aacute;ng cao gi&uacute;p trải nghiệm xem tr&ecirc;n m&aacute;y t&iacute;nh l&agrave; ho&agrave;n hảo.</p>\r\n\r\n<h3><br />\r\n<strong>Hiệu năng mạnh mẽ</strong></h3>\r\n\r\n<p><strong><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-03.jpg\" style=\"height:560px; width:840px\" /></strong></p>\r\n\r\n<p>Cung cấp sức mạnh cho HP Pavilion Power 15-cx0179TX/i5-8300H sẽ l&agrave; con chip Core i5 8300H thế hệ CPU Coffee Lake thứ 8 mới nhất của Intel. Kết hợp với 8GB RAM DDR4 v&agrave; ổ cứng 1TB HDD, m&aacute;y t&iacute;nh đủ sức lưu trữ rất nhiều tr&ograve; chơi mới nhất v&agrave; xử l&yacute; hầu hết c&aacute;c t&aacute;c vụ đa nhiệm một c&aacute;ch đơn giản. Để mọi thứ ho&agrave;n hảo hơn, HP trang bị cho m&aacute;y t&iacute;nh c&ocirc;ng nghệ Wi-Fi mới nhất, 2 quạt tản nhiệt v&agrave; camera g&oacute;c rộng 88 ở ph&iacute;a trước.</p>\r\n\r\n<h3><br />\r\n<strong>Card rời chuy&ecirc;n dụng</strong></h3>\r\n\r\n<p><strong><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-04.jpg\" style=\"height:560px; width:840px\" /></strong></p>\r\n\r\n<p>Sử dụng kiến tr&uacute;c NVIDIA Pascal ho&agrave;n to&agrave;n mới, HP Pavilion Power 15-cx0179TX/i5-8300H hứa hẹn sẽ l&agrave; một con qu&aacute;i vật trong xử l&yacute; đồ họa 3D. Card m&agrave;n h&igrave;nh rời NVIDIA GeForce GTX 1050 với bộ nhớ 4GB đảm bảo cho mọi t&aacute;c vụ chơi game, xem video, chỉnh sửa ảnh của bạn c&oacute; được hiệu ứng đồ họa ở mức cao nhất. Đối với game thủ, tr&ograve; chơi sẽ mở nhanh hơn v&agrave; mượt m&agrave; hơn nhờ c&ocirc;ng nghệ NVIDIA BatteryBoost.</p>\r\n\r\n<h3><br />\r\n<strong>&Acirc;m thanh sống động</strong></h3>\r\n\r\n<p><strong><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-05.jpg\" style=\"height:560px; width:840px\" /></strong></p>\r\n\r\n<p>Nhằm mục đ&iacute;ch cung cấp trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực, HP Pavilion Power 15-cx0179TX/i5-8300H c&oacute; hệ thống loa k&eacute;p với c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.&nbsp;</p>\r\n\r\n<h3><br />\r\n<strong>Sạc nhanh</strong></h3>\r\n\r\n<p><img alt=\"HP Pavilion Power 15\" src=\"https://fptshop.com.vn/Uploads/images/2015/SANPHAM/BinhNN4/HP%20Pavilion%20Power%2015%20i5/HP-15-CX0179TX-fptshop-06.jpg\" style=\"height:560px; width:840px\" /></p>\r\n\r\n<p>Một trong kh&aacute;c điểm cộng kh&aacute;c tr&ecirc;n HP Pavilion Power 15-cx0179TX/i5-8300H ch&iacute;nh l&agrave; vi&ecirc;n pin dung lượng cao 3 Cell, hỗ trợ sạc nhanh HP Fast Charge. Theo như nh&agrave; sản xuất cho biết thời gian sạc đầy chiếc laptop n&agrave;y chỉ l&agrave; 90 ph&uacute;t. Ngo&agrave;i ra, bạn cũng chỉ cần sạc 30 ph&uacute;t l&agrave; c&oacute; thể sử dụng thiết bị trong một khoảng thời gian d&agrave;i.</p>', 'HPPavilionGaming.png', 1, '2019-05-21 19:01:47', '2019-05-29 17:22:52'),
